    void pp() { // retorna pontos de personagem
        int r = cmbRaca.getSelectedIndex();
        int est = sliEstagio.getValue();
        int p = (int) spnPoder.getValue();
        int e = (int) spnEsp.getValue();
        int h = (int) spnHab.getValue();
        
        int pp = 0;      
        if (r==0 && est>=1) {
            pp = p+e+h+((est-1)*20);    
        }
        if (est>=1 && r !=0) {
            pp = p+e+h-3+((est-1)*20);
        }
        lblPP.setText(Integer.toString(pp));    
    }
    
    void pdl() { // retorna poder de luta
        int fo = Integer.parseInt(lblFor.getText());
        int agi = Integer.parseInt(lblAgi.getText());
        int res = Integer.parseInt(lblRes.getText());
        int ctl = Integer.parseInt(lblControle.getText());
        int ene = Integer.parseInt(lblEnergia.getText());
        int est = sliEstagio.getValue();
        int pp = Integer.parseInt(lblPP.getText());
        int estpdl = (int) Math.pow(10, est);
        lblPDL.setText(Integer.toString(((fo+agi+res+ctl+pp)*est)*estpdl+(ene*estpdl)));
    }
    
    private void spnPoderStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_spnPoderStateChanged
        int est = sliEstagio.getValue();
        int p = (int) spnPoder.getValue();
        int e = (int) spnEsp.getValue();
        int h = (int) spnHab.getValue();
        
        lblEnergia.setText(Integer.toString(p * 5 + e * 5 + h * 5)); // Energia = (Poder+Habilidade+Espírito)*5

        // Atributos Básicos
        lblFor.setText(Integer.toString((p + e) / 2)); // Força = ( Poder + Espírito ) / 2
        lblRes.setText(Integer.toString(p / 2 + ((e + h) / 4))); // Resistência = Poder / 2 + ((Espírito+Habilidade)/4)
        lblAgi.setText(Integer.toString(h / 2 + ((p + e) / 4))); // Agilidade = Habilidade / 2 + ( Espírito + Poder ) / 4 )
        lblControle.setText(Integer.toString((e + h) / 2)); // Controle = ( Espírito + Habilidade ) / 2

        // Combate
        lblAcc.setText(Integer.toString((p + h) / 2));
        lblAccEn.setText(Integer.toString((e + h) / 2));
        lblDef.setText(Integer.toString(h / 2 + ((p + e) / 4)));
        
        pp(); // procedimento gera pontos de personagem
        
        pdl(); // procedimento gera poder de luta


    }//GEN-LAST:event_spnPoderStateChanged

