    private void cmbRacaItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cmbRacaItemStateChanged
        int est = sliEstagio.getValue();
        int p = (int) spnPoder.getValue();
        int e = (int) spnEsp.getValue();
        int h = (int) spnHab.getValue();
        int r = cmbRaca.getSelectedIndex();
        switch (r) {
            case 1:
                spnHab.setModel(new javax.swing.SpinnerNumberModel(1, 1, 10, 1));
                spnPoder.setModel(new javax.swing.SpinnerNumberModel(0, 0, 10, 1));
                spnEsp.setModel(new javax.swing.SpinnerNumberModel(2, 2, 10, 1));
                break;
            case 2:
                spnPoder.setModel(new javax.swing.SpinnerNumberModel(2, 2, 10, 1));
                spnEsp.setModel(new javax.swing.SpinnerNumberModel(1, 1, 10, 1));
                spnHab.setModel(new javax.swing.SpinnerNumberModel(0, 0, 10, 1));
                break;
            case 3:
                spnPoder.setModel(new javax.swing.SpinnerNumberModel(2, 2, 10, 1));
                spnHab.setModel(new javax.swing.SpinnerNumberModel(1, 1, 10, 1));
                spnEsp.setModel(new javax.swing.SpinnerNumberModel(0, 0, 10, 1));
                break;
            case 4:
                spnHab.setModel(new javax.swing.SpinnerNumberModel(2, 2, 10, 1));
                spnEsp.setModel(new javax.swing.SpinnerNumberModel(1, 1, 10, 1));
                spnPoder.setModel(new javax.swing.SpinnerNumberModel(0, 0, 10, 1));
                break;
            case 5:
                spnPoder.setModel(new javax.swing.SpinnerNumberModel(3, 3, 10, 1));
                spnHab.setModel(new javax.swing.SpinnerNumberModel(0, 0, 10, 1));
                spnEsp.setModel(new javax.swing.SpinnerNumberModel(0, 0, 10, 1));
                break;
            default:
                spnHab.setModel(new javax.swing.SpinnerNumberModel(0, 0, 10, 1));
                spnPoder.setModel(new javax.swing.SpinnerNumberModel(0, 0, 10, 1));
                spnEsp.setModel(new javax.swing.SpinnerNumberModel(0, 0, 10, 1));
                break;
        }
        lblEnergia.setText(Integer.toString(p * 5 + e * 5 + h * 5)); // Energia = (Poder+Habilidade+Espírito)*5

        // Atributos Básicos
        lblFor.setText(Integer.toString((p + e) / 2)); // Força = ( Poder + Espírito ) / 2
        lblRes.setText(Integer.toString(p / 2 + ((e + h) / 4))); // Resistência = Poder / 2 + ((Espírito+Habilidade)/4)
        lblAgi.setText(Integer.toString(h / 2 + ((p + e) / 4))); // Agilidade = Habilidade / 2 + ( Espírito + Poder ) / 4 )
        lblControle.setText(Integer.toString((e + h) / 2)); // Controle = ( Espírito + Habilidade ) / 2

        // Combate
        lblAcc.setText(Integer.toString((p + h) / 2));
        lblAccEn.setText(Integer.toString((e + h) / 2));
        lblDef.setText(Integer.toString(h / 2 + ((p + e) / 4)));
        
        pp(); // procedimento gera pontos de personagem
        
        pdl(); // procedimento gera poder de luta
    }//GEN-LAST:event_cmbRacaItemStateChanged

